mapper.select:

where sys_role.deleted = false

    AND sys_role.role_name like concat('%', #{roleName}, '%')


    AND sys_role.status = #{status}


    AND sys_role.role_key like concat('%', #{roleKey}, '%')


    AND sys_role.data_scope = #{dataScope}


    and date_format(sys_role.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


    and date_format(sys_role.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')


${params.dataScope}
